export const Features = (props) => {
  const pagesDefinition = require("../data/data.json");
  return (
    <div id='features' className='text-center'>
      <div className='container'>
        <div className='section-title'>
          <h2>{pagesDefinition.titleFeatures}</h2>
          <p>
            {pagesDefinition.paragraphFeatures}
          </p>
        </div>
        <div className='row'>
          {props.data
            ? props.data.map((d, i) => (
                <div key={`${d.title}-${i}`} className='col-xs-6 col-md-3'>
                  {' '}
                  <img src={d.img} className="img-responsive" alt=""/>
                  <h2>{d.title}</h2>
                  <h3>{d.text}</h3>
                  <p>{d.subText}</p>
                </div>
              ))
            : 'Loading...'}
        </div>
      </div>
    </div>
  )
}
