import { useState, useEffect } from "react";
import { Navigation } from "./components/navigation";
import { Header } from "./components/header";
import { Features } from "./components/features";
import { Services } from "./components/services";
import { Contact } from "./components/contact";
import { Footer} from "./components/footer";
import JsonData from "./data/data.json";
import SmoothScroll from "smooth-scroll";
import "./App.css";
import "../public/js/main"


export const scroll = new SmoothScroll('a[href*="#"]', {
  speed: 1000,
  speedAsDuration: true,
});

const App = () => {
  const [landingPageData, setLandingPageData] = useState({});
  useEffect(() => {
    setLandingPageData(JsonData);
  }, []);

  const paginas = [];

  for (const item of JsonData.pages) {
    if (item.pageName === "Navigation") {
      {/*paginas.push({pagina: <Navigation/>, orden: item.order});*/}
    } else if (item.pageName === "Header") {
      paginas.push({pagina: <Header data={landingPageData.Header}/>, orden: item.order});
    } else if (item.pageName === "About") {
      {/*paginas.push({pagina: <About data={landingPageData.About}/>, orden: item.order});*/}
    } else if (item.pageName === "Features") {
      {/*paginas.push({pagina: <Features data={landingPageData.Features}/>, orden: item.order});*/}
    } else if (item.pageName === "Services") {
      {/*paginas.push({pagina: <Services data={landingPageData.Services}/>, orden: item.order});*/}
    } else if (item.pageName === "Contact") {
      paginas.push({pagina: <Contact data={landingPageData.Contact}/>, orden: item.order});
    } else if (item.pageName === "Footer") {
      paginas.push({pagina: <Footer data={landingPageData.Footer}/>, orden: item.order});
    }
  }
  
  return (
    <div>
      {
        paginas.sort((a,b) => a.orden-b.orden).map(e => e.pagina)
      }
    </div>
  );
};

export default App;
