export const Services = (props) => {
  const pagesDefinition = require("../data/data.json");
  

  return (
    <div id='services' className='text-center'>
      <div className='container'>
        <div className='section-title'>
          <h2>{pagesDefinition.titleServices}</h2>
          <p>
            {pagesDefinition.paragraphServices}
          </p>
        </div>
        <div className='row'>
          {props.data ? props.data.map((d, i) => (
            <div key={`${d.title}-${i}`} {...`${d.number} % 2 === 0` ? className='right' : className='left'}>
                {' '}
                <img src={d.img} className="img-responsive" alt="" />
                <div className='service-desc'>
                  <h3>{d.title}</h3>
                  <h4>{d.text}</h4>
                  <p>{d.subText}</p>
                </div>
              </div>
            )) : 'Loading...'}
        </div>
      </div>
    </div>
  )
}
